import React, { Component } from 'react';
import PropTypes from "prop-types";
import axios from 'axios';
import './App.css';

class App extends Component {
	state = {
		price : []
	}

	componentDidMount() {
		axios.get(`https://min-api.cryptocompare.com/data/price?fsym=BTC&tsyms=USD,JPY,EUR`)
			.then(res => {
				const price = this.state.price.map(price =>{
					console.log(res.data);
					return price
				})
				this.setState({ price });
			})
	}

	static defaultProps = {
        pageTitle : "Live Cryptocurrency"
    }
    static propTypes = {
        pageTitle : PropTypes.string.isRequired
	}
	handleRefresh = () => {
		window.location.reload(); 
	}

	render() {
		return (
			<div className="bx">
				<header>
					<h1>{this.props.pageTitle}</h1>
					<p className="countdown">60</p>
					<button className="btn_reload" onClick={this.handleRefresh}>
						<span className="blind">Refresh</span>
					</button>
				</header>
				<article>
				<table className="type09">
					<caption>Cryptocurrency Live</caption>
					<thead>
						<tr>
							<th><a href="https://cryptowat.ch/markets/bithumb/btc/krw">BTC/USD</a></th>
							<th><a href="https://cryptowat.ch/markets/bithumb/eth/krw">ETH/USD</a></th>
							<th><a href="https://cryptowat.ch/markets/bithumb/xrp/krw">XRP/USD</a></th>
						</tr>
					</thead>
					<tbody>
						<tr>
							<td className="active">
								<a href="https://cryptowat.ch/markets/bithumb/btc/krw">
									<div>$ {this.state.price.USD}</div>
								</a>
							</td>
							<td className="active">
								<a href="https://cryptowat.ch/markets/bithumb/eth/krw">
								<div>$ {this.state.price.USD}</div>
								</a>
							</td>
							<td className="active">
								<a href="https://cryptowat.ch/markets/bithumb/xrp/krw">
								<div>$ {this.state.price.USD}</div>
								</a>
							</td>
						</tr>
						<tr>
							<td className="active">
								<a href="https://cryptowat.ch/markets/bithumb/btc/krw">
									<div>0.25 %</div>
								</a>
							</td>
							<td className="active">
								<a href="https://cryptowat.ch/markets/bithumb/eth/krw">
									<div>-3.35 %</div>
								</a>
							</td>
							<td className="active">
								<a href="https://cryptowat.ch/markets/bithumb/xrp/krw">
									<div>-0.26 %</div>
								</a>
							</td>
						</tr>
					</tbody>
				</table>
				</article>
			</div>
		);
	}
}

export default App;
